    rangeCheckForAdd(index); // \u68C0\u67E5\u7D22\u5F15\u662F\u5426\u8D8A\u754C
	// \u786E\u4FDD\u5BB9\u91CF\u8DB3\u591F\uFF0C\u5982\u679C\u9700\u8981\u6269\u5BB9\u5C31\u6269\u5BB9
    ensureCapacityInternal(size + 1);  
    // \u5C06 index \u53CA\u5176\u540E\u9762\u7684\u5143\u7D20\u5411\u540E\u79FB\u52A8\u4E00\u4F4D
